import React from 'react';
import ProjectSideSection from './components/ProjectSideSection/ProjectSideSection.js';
import airterra from './assets/img/sides/airterra.png';
import email from './assets/img/sides/email.png';
import aspecta from './assets/img/sides/aspecta.png';
const Side = (props) => {
	const controls = [
		{ 
	    	title: 'Airterra',
			category: 'Pitch Deck Design',
			description: '',
			img: airterra,
		},
	    { 
	    	title: 'Email Design & Development',
			category: 'Salesforce Marketing Cloud',
			description: 'Fill the gap. Created design system for email marketing from scratch. Developed 30+ responsive emails using HTML and CSS.',
			img: email,
		},
	    { 
	    	title: 'Aspecta',
			category: 'UI/UX Design | Design System',
			description: 'Fill the gap. Created design system for email marketing from scratch. Developed 30+ responsive emails using HTML and CSS.',
			img: aspecta,
		}

	];
	return (
	<div className="project-box">
		<div className="img-container grid grid-gap-xs">
			<div className="col-12 flex flex-center">
				<p>Previously Front-end Engineer Intern <a href="">@Lenovo</a></p>

					<h5><strong>Front-end: </strong> React.js | Redux | React-Router | JavaScript(ES6) | HTML | CSS | Bootstrap | jQuery | D3.js</h5>
					<h5><strong>Back-end: </strong> Node.js | PHP | SQL(MySQL) | NoSQL (MongoDB) | Express | socket.io</h5>
			</div>
    	</div>
		<div className="portfolio-content portfolio-content-coding grid flex-width-80 grid-gap-md">

		{controls.map(project => (
            <ProjectSideSection 
            	key = {project.title} 
                title={project.title} 
                category={project.category} 
                description={project.description}
                img = {project.img}
            />
        ))}
        </div>
	</div>
)}
export default Side;
